import { Command } from '@author.io/node-shell'
import { spawn } from 'child_process'

const UpdateCommand = new Command({
  name: 'update',
  description: 'Update the CLI tool.',
  handler (meta) {
    spawn('npm update -g @author.io/dev', { shell: true, stdio: [0,1,2] })
  }
})

export { UpdateCommand as default }
